INSERT INTO `wp_testimonials` VALUES(13, 0, 0, '2011-02-19', 'Claudia Dahdoub', 'Phoenix, Az', 'Brain lesions gone!', 'I was diagnosed with potential lesions on my brian via MRI.  [During Prayer] ..felt God\\''s presence, [after 2nd MRI] all is normal!', '');
INSERT INTO `wp_testimonials` VALUES(12, 0, 0, '2011-02-19', 'Amy', 'Mesa, Az', 'Healed of Cancer	', 'I was told I probably had cancerous tumors on my thyroid and [after prayer] one of the tumors couldn\\''t be found.	', '');
INSERT INTO `wp_testimonials` VALUES(14, 0, 0, '2011-02-24', 'Mary V.', 'Phoenix, AZ', 'Arteries cleared!', 'Dr. Said I needed my left ventricle cleared with a stent because of blockage. I had a lot of Fear and Panic Attacks. In prayer room I felt the presence of peace and assurance of a blessing. No stent was needed and my arteries are now clear!', '');
INSERT INTO `wp_testimonials` VALUES(15, 0, 0, '2011-02-24', 'Pat S.', 'Gilbert, AZ', 'Short leg grows!', 'My left hip/leg was shorter than the right. The Team anointed me with oil. I sat on a chair. The scriptures were read and my leg grew out! Wonderful! The Word of God is powerful!', '');
INSERT INTO `wp_testimonials` VALUES(16, 0, 0, '2011-02-24', 'Patrick M.', 'Phoenix, AZ', 'Back Disc Healed!', 'Bulging disc in back, ling congestion and knee stiffness healed and I have the peace of God.', '');
INSERT INTO `wp_testimonials` VALUES(17, 0, 0, '2011-02-24', 'Laurie E.', 'Phoenix, AZ', 'Vertigo Healed!', 'My condition: Vertigo. I had an excellent experience in the prayer room and I am totally healed - no vertigo since!', '');
INSERT INTO `wp_testimonials` VALUES(18, 0, 0, '2011-02-24', 'Dennis W.', 'Chandler, AZ', 'Lactose intolerance', 'I got prayer from the AHR and got totally healed from lactose intolerance! Yay God!', '');
INSERT INTO `wp_testimonials` VALUES(19, 0, 0, '2011-02-24', 'Lora S.', 'Maricopa, AZ', 'Ears Opened!', 'My Ears were stopped up, muffled hearing. In prayer room my ears opened! I now hear clearly.	', '');
